    private void submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitActionPerformed
        try
        {
            this.tien = Integer.parseInt(this.tongtien.getText());
            this.dispose();
        }
        catch (Exception ex)
        {
            JOptionPane.showMessageDialog(this, "Không thể sử dụng ký tự này hoặc để trống");
        }
    }//GEN-LAST:event_submitActionPerformed

